CREATE TABLE `<<__prefix__>>clan` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `ownerId` BIGINT NOT NULL , `name` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL , `description` TEXT CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL , `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updatedAt` DATETIME on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;